mapper.select:
select t.title as typeTitle, l.title as levelTitle, u.en_name as userName, u.third_head_img_path as userThirdHeadImgPath, u.third_type as thirdType, s.user_id as userId, s.consume_time as consumeTime, s.create_time as createTime from t_game_score s inner join t_num_game_level l on s.game_level_id = l.id inner join t_num_game_type t on s.game_type_id = t.id inner join t_user u on s.user_id =u.id where u.status ='available' and l.status ='available' and t.status ='available' and s.consume_time is not null
and u.third_type=#{thirdType}
and t.id =#{gameTypeId}
and l.id =#{gameLevelId}
order by s.consume_time
